                Main.npc[arm].ai[0] = 1f;
                Main.npc[arm].ai[1] = npc.whoAmI;
                Main.npc[arm].target = npc.target;
                Main.npc[arm].netUpdate = true;
                Main.npc[arm].ai[3] = 150f;
