                npc.velocity.X -= acceleration;
                if (npc.velocity.X > 0f && distanceVector.X < 0f)
                {
                    npc.velocity.X -= acceleration * 1.5f;
                }
            }
            if (npc.position.Y < npc.ai[1] * 16f + 8f + distanceVector.Y)
